mapper.update:

update FCAPTOT


    FCAPTOT_CNT_DR = NVL(FCAPTOT_CNT_DR, 0) + #{fcaptotCntDr,jdbcType=INTEGER},


    FCAPTOT_AMT_DR = NVL(FCAPTOT_AMT_DR, 0) + #{fcaptotAmtDr,jdbcType=DECIMAL},


    FCAPTOT_CNT_CR = NVL(FCAPTOT_CNT_CR, 0) + #{fcaptotCntCr,jdbcType=INTEGER},


    FCAPTOT_AMT_CR = NVL(FCAPTOT_AMT_CR, 0) + #{fcaptotAmtCr,jdbcType=DECIMAL},


    UPDATE_USERID = #{updateUserid,jdbcType=DECIMAL},

  UPDATE_TIME = SYSDATE,

where FCAPTOT_ST_DATE = #{fcaptotStDate,jdbcType=CHAR}
  and FCAPTOT_CURRENCY = #{fcaptotCurrency,jdbcType=CHAR}
  and FCAPTOT_APID = #{fcaptotApid,jdbcType=CHAR}
  and FCAPTOT_BRNO = #{fcaptotBrno,jdbcType=CHAR}
