mapper.update:

update RMFISCOUT1
set
  RMFISCOUT1_REP_NO = #{rmfiscout1RepNo,jdbcType=DECIMAL}
where RMFISCOUT1_SENDER_BANK = #{rmfiscout1SenderBank,jdbcType=CHAR}
  and RMFISCOUT1_RECEIVER_BANK = #{rmfiscout1ReceiverBank,jdbcType=CHAR}
AND RMFISCOUT1_REP_NO < #{rmfiscout1RepNo,jdbcType=DECIMAL}

mapper.select:
SELECT

FROM RMFISCOUT1
where RMFISCOUT1_SENDER_BANK = #{rmfiscout1SenderBank,jdbcType=CHAR}
  and RMFISCOUT1_RECEIVER_BANK = #{rmfiscout1ReceiverBank,jdbcType=CHAR}
