mapper.select:

select

from RMINT
WHERE RMINT_TXDATE = #{rmintTxdate,jdbcType=CHAR}
AND RMINT_PENDING = #{rmintPending,jdbcType=CHAR}
AND RMINT_FISC_RTN_CODE = #{rmintFiscRtnCode,jdbcType=CHAR}
AND RMINT_STAT IN ('99','07')
ORDER BY RMINT_BRNO,RMINT_FEPNO,RMINT_TXDATE

SELECT RMINT_TXDATE AS TXDATE, NVL(RMINT_SENDTIME,' ') AS SENDTIME, NVL(RMINT_SENDER_BANK,' ') AS SENDER_BANK, NVL(RMINT_RECEIVER_BANK,' ') AS RECEIVER_BANK, NVL(RMINT_IN_ACC_ID_NO,' ') AS ACTNO, NVL(RMINT_TXAMT,'0') AS AMT, NVL(RMINT_STAT,' ') AS STAT, NVL(RMINT_FISC_RTN_CODE,' ') AS RC from RMINT WHERE RMINT_TXDATE = #{txdate,jdbcType=CHAR}
AND SUBSTR(RMINT_RECEIVER_BANK,4,3) = #{brno,jdbcType=CHAR}
AND RMINT_STAT IN ('04','99')
