select

from RMOUT
where RMOUT_TXDATE = #{rmoutTxdate,jdbcType=CHAR}
and RMOUT_BRNO = #{rmoutBrno,jdbcType=CHAR}
and RMOUT_ORIGINAL = #{rmoutOriginal,jdbcType=CHAR}
and RMOUT_FEPNO = #{rmoutFepno,jdbcType=CHAR}
FOR UPDATE

select COUNT(*) as CNT, RMOUT_ORIGINAL
From RMOUT
where RMOUT_TXDATE = #{rmoutTxdate,jdbcType=CHAR}
AND RMOUT_FISC_RTN_CODE = #{code,jdbcType=CHAR}
GROUP BY RMOUT_ORIGINAL

Select COUNT(*) as CNT, RMOUT_ORIGINAL
From RMIN INNER JOIN RMOUT ON RMOUT.RMOUT_TXDATE = RMIN.RMIN_ORGDATE
AND RMOUT.RMOUT_RMSNO = RMIN.RMIN_ORGRMSNO
AND "SUBSTR"(RMIN.RMIN_SENDER_BANK, 1, 3) = "SUBSTR"(RMOUT.RMOUT_RECEIVER_BANK, 1, 3)
where RMIN.RMIN_TXDATE = #{txdate,jdbcType=CHAR}
AND RMIN.RMIN_FISC_SND_CODE = #{sndCode,jdbcType=CHAR}
AND RMOUT.RMOUT_STAT = #{stat,jdbcType=CHAR}
AND RMIN.RMIN_FISC_RTN_CODE = #{rtnCode,jdbcType=CHAR}
GROUP BY RMOUT.RMOUT_ORIGINAL

select COUNT(*) as CNT, RMOUT_ORIGINAL
from RMIN INNER JOIN RMOUT ON RMOUT.RMOUT_TXDATE = RMIN.RMIN_ORGDATE
AND RMOUT.RMOUT_RMSNO = RMIN.RMIN_ORGRMSNO AND
SUBSTR(RMIN.RMIN_SENDER_BANK, 1, 3) = SUBSTR(RMOUT.RMOUT_RECEIVER_BANK, 1, 3)
where RMIN.RMIN_TXDATE = #{txdate,jdbcType=CHAR}
AND RMIN.RMIN_FISC_SND_CODE = #{sndCode,jdbcType=CHAR}
AND RMOUT.RMOUT_STAT = #{stat,jdbcType=CHAR}
AND RMIN.RMIN_FISC_RTN_CODE = #{rtnCode,jdbcType=CHAR}
GROUP BY RMOUT.RMOUT_ORIGINAL

