mapper.select:

select 

from BITMAPDEF
where BITMAPDEF_MSGTYPE = #{bitmapdefMsgtype,jdbcType=CHAR}
  and BITMAPDEF_PCODE = #{bitmapdefPcode,jdbcType=CHAR}

mapper.delete:

delete from BITMAPDEF
where BITMAPDEF_MSGTYPE = #{bitmapdefMsgtype,jdbcType=CHAR}
  and BITMAPDEF_PCODE = #{bitmapdefPcode,jdbcType=CHAR}

mapper.update:

update BITMAPDEF
set BITMAPDEF_BITMAP_LIST = #{bitmapdefBitmapList,jdbcType=VARCHAR},
  BITMAPDEF_TYPE = #{bitmapdefType,jdbcType=DECIMAL},
  BITMAPDEF_CHECK_TBSDY = #{bitmapdefCheckTbsdy,jdbcType=SMALLINT},
  BITMAPDEF_TIMEOUT = #{bitmapdefTimeout,jdbcType=INTEGER},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where BITMAPDEF_MSGTYPE = #{bitmapdefMsgtype,jdbcType=CHAR}
  and BITMAPDEF_PCODE = #{bitmapdefPcode,jdbcType=CHAR}
