mapper.select:

select 

from CBACT
where CBACT_TYPE = #{cbactType,jdbcType=VARCHAR}
  and CBACT_BKNO = #{cbactBkno,jdbcType=CHAR}
  and CBACT_ACTNO = #{cbactActno,jdbcType=VARCHAR}
  and CBACT_ACTNO_LEN = #{cbactActnoLen,jdbcType=DECIMAL}

mapper.delete:

delete from CBACT
where CBACT_TYPE = #{cbactType,jdbcType=VARCHAR}
  and CBACT_BKNO = #{cbactBkno,jdbcType=CHAR}
  and CBACT_ACTNO = #{cbactActno,jdbcType=VARCHAR}
  and CBACT_ACTNO_LEN = #{cbactActnoLen,jdbcType=DECIMAL}

mapper.update:

update CBACT


    CBACT_EFFECT_DATE = #{cbactEffectDate,jdbcType=CHAR},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where CBACT_TYPE = #{cbactType,jdbcType=VARCHAR}
  and CBACT_BKNO = #{cbactBkno,jdbcType=CHAR}
  and CBACT_ACTNO = #{cbactActno,jdbcType=VARCHAR}
  and CBACT_ACTNO_LEN = #{cbactActnoLen,jdbcType=DECIMAL}


update CBACT
set CBACT_EFFECT_DATE = #{cbactEffectDate,jdbcType=CHAR},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where CBACT_TYPE = #{cbactType,jdbcType=VARCHAR}
  and CBACT_BKNO = #{cbactBkno,jdbcType=CHAR}
  and CBACT_ACTNO = #{cbactActno,jdbcType=VARCHAR}
  and CBACT_ACTNO_LEN = #{cbactActnoLen,jdbcType=DECIMAL}
