mapper.select:

select 

from CLRDTL
where CLRDTL_TXDATE = #{clrdtlTxdate,jdbcType=CHAR}
  and CLRDTL_AP_ID = #{clrdtlApId,jdbcType=CHAR}
  and CLRDTL_PAYTYPE = #{clrdtlPaytype,jdbcType=VARCHAR}

mapper.delete:

delete from CLRDTL
where CLRDTL_TXDATE = #{clrdtlTxdate,jdbcType=CHAR}
  and CLRDTL_AP_ID = #{clrdtlApId,jdbcType=CHAR}
  and CLRDTL_PAYTYPE = #{clrdtlPaytype,jdbcType=VARCHAR}
