mapper.select:

select 

from CLRTOTAL
where CLRTOTAL_ST_DATE = #{clrtotalStDate,jdbcType=CHAR}
  and CLRTOTAL_CUR = #{clrtotalCur,jdbcType=CHAR}
  and CLRTOTAL_SOURCE = #{clrtotalSource,jdbcType=DECIMAL}

mapper.delete:

delete from CLRTOTAL
where CLRTOTAL_ST_DATE = #{clrtotalStDate,jdbcType=CHAR}
  and CLRTOTAL_CUR = #{clrtotalCur,jdbcType=CHAR}
  and CLRTOTAL_SOURCE = #{clrtotalSource,jdbcType=DECIMAL}
