mapper.select:

select 

from EMVC
where EMVC_TX_DATE = #{emvcTxDate,jdbcType=CHAR}
  and EMVC_PAN = #{emvcPan,jdbcType=VARCHAR}
  and EMVC_CURCD = #{emvcCurcd,jdbcType=CHAR}

mapper.delete:

delete from EMVC
where EMVC_TX_DATE = #{emvcTxDate,jdbcType=CHAR}
  and EMVC_PAN = #{emvcPan,jdbcType=VARCHAR}
  and EMVC_CURCD = #{emvcCurcd,jdbcType=CHAR}

mapper.update:

update EMVC


    EMVC_TX_CNT = #{emvcTxCnt,jdbcType=INTEGER},


    EMVC_TX_AMT = #{emvcTxAmt,jdbcType=DECIMAL},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where EMVC_TX_DATE = #{emvcTxDate,jdbcType=CHAR}
  and EMVC_PAN = #{emvcPan,jdbcType=VARCHAR}
  and EMVC_CURCD = #{emvcCurcd,jdbcType=CHAR}


update EMVC
set EMVC_TX_CNT = #{emvcTxCnt,jdbcType=INTEGER},
  EMVC_TX_AMT = #{emvcTxAmt,jdbcType=DECIMAL},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where EMVC_TX_DATE = #{emvcTxDate,jdbcType=CHAR}
  and EMVC_PAN = #{emvcPan,jdbcType=VARCHAR}
  and EMVC_CURCD = #{emvcCurcd,jdbcType=CHAR}
