mapper.select:

select 

from ENCCTL
where MSGID = #{msgid,jdbcType=VARCHAR}
  and TXNID = #{txnid,jdbcType=VARCHAR}
  and CHLNAME = #{chlname,jdbcType=VARCHAR}

mapper.delete:

delete from ENCCTL
where MSGID = #{msgid,jdbcType=VARCHAR}
  and TXNID = #{txnid,jdbcType=VARCHAR}
  and CHLNAME = #{chlname,jdbcType=VARCHAR}

mapper.update:

update ENCCTL


    EFFECTDATE = #{effectdate,jdbcType=VARCHAR},


    EXPIREDATE = #{expiredate,jdbcType=VARCHAR},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where MSGID = #{msgid,jdbcType=VARCHAR}
  and TXNID = #{txnid,jdbcType=VARCHAR}
  and CHLNAME = #{chlname,jdbcType=VARCHAR}


update ENCCTL
set EFFECTDATE = #{effectdate,jdbcType=VARCHAR},
  EXPIREDATE = #{expiredate,jdbcType=VARCHAR},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where MSGID = #{msgid,jdbcType=VARCHAR}
  and TXNID = #{txnid,jdbcType=VARCHAR}
  and CHLNAME = #{chlname,jdbcType=VARCHAR}
