mapper.select:

select 

from EXRATE
where EXRATE_ZONE = #{exrateZone,jdbcType=CHAR}
  and EXRATE_CUR = #{exrateCur,jdbcType=CHAR}

mapper.delete:

delete from EXRATE
where EXRATE_ZONE = #{exrateZone,jdbcType=CHAR}
  and EXRATE_CUR = #{exrateCur,jdbcType=CHAR}

mapper.update:

update EXRATE
set EXRATE_RATE = #{exrateRate,jdbcType=DECIMAL},
  EXRATE_SCASH = #{exrateScash,jdbcType=DECIMAL},
  EXRATE_DATE = #{exrateDate,jdbcType=CHAR},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where EXRATE_ZONE = #{exrateZone,jdbcType=CHAR}
  and EXRATE_CUR = #{exrateCur,jdbcType=CHAR}
