mapper.select:

select 

from FCAPTOT
where FCAPTOT_ST_DATE = #{fcaptotStDate,jdbcType=CHAR}
  and FCAPTOT_CURRENCY = #{fcaptotCurrency,jdbcType=CHAR}
  and FCAPTOT_APID = #{fcaptotApid,jdbcType=CHAR}
  and FCAPTOT_BRNO = #{fcaptotBrno,jdbcType=CHAR}

mapper.delete:

delete from FCAPTOT
where FCAPTOT_ST_DATE = #{fcaptotStDate,jdbcType=CHAR}
  and FCAPTOT_CURRENCY = #{fcaptotCurrency,jdbcType=CHAR}
  and FCAPTOT_APID = #{fcaptotApid,jdbcType=CHAR}
  and FCAPTOT_BRNO = #{fcaptotBrno,jdbcType=CHAR}

mapper.update:

update FCAPTOT


    FCAPTOT_CNT_DR = #{fcaptotCntDr,jdbcType=INTEGER},


    FCAPTOT_AMT_DR = #{fcaptotAmtDr,jdbcType=DECIMAL},


    FCAPTOT_CNT_CR = #{fcaptotCntCr,jdbcType=INTEGER},


    FCAPTOT_AMT_CR = #{fcaptotAmtCr,jdbcType=DECIMAL},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where FCAPTOT_ST_DATE = #{fcaptotStDate,jdbcType=CHAR}
  and FCAPTOT_CURRENCY = #{fcaptotCurrency,jdbcType=CHAR}
  and FCAPTOT_APID = #{fcaptotApid,jdbcType=CHAR}
  and FCAPTOT_BRNO = #{fcaptotBrno,jdbcType=CHAR}


update FCAPTOT
set FCAPTOT_CNT_DR = #{fcaptotCntDr,jdbcType=INTEGER},
  FCAPTOT_AMT_DR = #{fcaptotAmtDr,jdbcType=DECIMAL},
  FCAPTOT_CNT_CR = #{fcaptotCntCr,jdbcType=INTEGER},
  FCAPTOT_AMT_CR = #{fcaptotAmtCr,jdbcType=DECIMAL},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where FCAPTOT_ST_DATE = #{fcaptotStDate,jdbcType=CHAR}
  and FCAPTOT_CURRENCY = #{fcaptotCurrency,jdbcType=CHAR}
  and FCAPTOT_APID = #{fcaptotApid,jdbcType=CHAR}
  and FCAPTOT_BRNO = #{fcaptotBrno,jdbcType=CHAR}
