mapper.select:

select 

from FCRMPOST
where FCRMPOST_POSTID = #{fcrmpostPostid,jdbcType=CHAR}
  and FCRMPOST_EFFECTDATE = #{fcrmpostEffectdate,jdbcType=CHAR}

mapper.delete:

delete from FCRMPOST
where FCRMPOST_POSTID = #{fcrmpostPostid,jdbcType=CHAR}
  and FCRMPOST_EFFECTDATE = #{fcrmpostEffectdate,jdbcType=CHAR}
