mapper.select:

select 

from FCRMTOT
where FCRMTOT_TXDATE = #{fcrmtotTxdate,jdbcType=CHAR}
  and FCRMTOT_BRNO = #{fcrmtotBrno,jdbcType=CHAR}
  and FCRMTOT_CURRENCY = #{fcrmtotCurrency,jdbcType=CHAR}

mapper.delete:

delete from FCRMTOT
where FCRMTOT_TXDATE = #{fcrmtotTxdate,jdbcType=CHAR}
  and FCRMTOT_BRNO = #{fcrmtotBrno,jdbcType=CHAR}
  and FCRMTOT_CURRENCY = #{fcrmtotCurrency,jdbcType=CHAR}
