  ACCODE, SUBACCODE, DTLACCODE
  )
values (#{syscode,jdbcType=CHAR}, #{txdate,jdbcType=DATE}, #{txreccount,jdbcType=DECIMAL}, 
  #{txdramt,jdbcType=DECIMAL}, #{txcramt,jdbcType=DECIMAL}, #{upduserid,jdbcType=CHAR}, 
  #{upddatetime,jdbcType=TIMESTAMP}, #{currencycode,jdbcType=VARCHAR}, #{acbranchcode,jdbcType=VARCHAR}, 
  #{accode,jdbcType=VARCHAR}, #{subaccode,jdbcType=VARCHAR}, #{dtlaccode,jdbcType=VARCHAR}
  )
