mapper.select:

select 

,

from MSGFILE
where MSGFILE_CHANNEL = #{msgfileChannel,jdbcType=INTEGER}
  and MSGFILE_ERRORCODE = #{msgfileErrorcode,jdbcType=VARCHAR}

mapper.delete:

delete from MSGFILE
where MSGFILE_CHANNEL = #{msgfileChannel,jdbcType=INTEGER}
  and MSGFILE_ERRORCODE = #{msgfileErrorcode,jdbcType=VARCHAR}
