mapper.select:

select 

from ODRC
where ODRC_TX_DATE = #{odrcTxDate,jdbcType=CHAR}
  and ODRC_BKNO = #{odrcBkno,jdbcType=CHAR}
  and ODRC_ACTNO = #{odrcActno,jdbcType=CHAR}
  and ODRC_CURCD = #{odrcCurcd,jdbcType=CHAR}

mapper.delete:

delete from ODRC
where ODRC_TX_DATE = #{odrcTxDate,jdbcType=CHAR}
  and ODRC_BKNO = #{odrcBkno,jdbcType=CHAR}
  and ODRC_ACTNO = #{odrcActno,jdbcType=CHAR}
  and ODRC_CURCD = #{odrcCurcd,jdbcType=CHAR}

mapper.update:

update ODRC


    ODRC_TX_CNT = #{odrcTxCnt,jdbcType=INTEGER},


    ODRC_TX_AMT = #{odrcTxAmt,jdbcType=DECIMAL},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where ODRC_TX_DATE = #{odrcTxDate,jdbcType=CHAR}
  and ODRC_BKNO = #{odrcBkno,jdbcType=CHAR}
  and ODRC_ACTNO = #{odrcActno,jdbcType=CHAR}
  and ODRC_CURCD = #{odrcCurcd,jdbcType=CHAR}


update ODRC
set ODRC_TX_CNT = #{odrcTxCnt,jdbcType=INTEGER},
  ODRC_TX_AMT = #{odrcTxAmt,jdbcType=DECIMAL},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where ODRC_TX_DATE = #{odrcTxDate,jdbcType=CHAR}
  and ODRC_BKNO = #{odrcBkno,jdbcType=CHAR}
  and ODRC_ACTNO = #{odrcActno,jdbcType=CHAR}
  and ODRC_CURCD = #{odrcCurcd,jdbcType=CHAR}
