mapper.select:

select 

from QREPORTDATA
where QR_TBSDY_INTL = #{qrTbsdyIntl,jdbcType=CHAR}
  and QR_PCODE = #{qrPcode,jdbcType=CHAR}
  and QR_ZONE_CODE = #{qrZoneCode,jdbcType=CHAR}
  and QR_ACQ_CNTRY = #{qrAcqCntry,jdbcType=CHAR}

mapper.delete:

delete from QREPORTDATA
where QR_TBSDY_INTL = #{qrTbsdyIntl,jdbcType=CHAR}
  and QR_PCODE = #{qrPcode,jdbcType=CHAR}
  and QR_ZONE_CODE = #{qrZoneCode,jdbcType=CHAR}
  and QR_ACQ_CNTRY = #{qrAcqCntry,jdbcType=CHAR}

mapper.update:

update QREPORTDATA


    QR_CNT = #{qrCnt,jdbcType=INTEGER},


    QR_AMT = #{qrAmt,jdbcType=DECIMAL},


    UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


where QR_TBSDY_INTL = #{qrTbsdyIntl,jdbcType=CHAR}
  and QR_PCODE = #{qrPcode,jdbcType=CHAR}
  and QR_ZONE_CODE = #{qrZoneCode,jdbcType=CHAR}
  and QR_ACQ_CNTRY = #{qrAcqCntry,jdbcType=CHAR}


update QREPORTDATA
set QR_CNT = #{qrCnt,jdbcType=INTEGER},
  QR_AMT = #{qrAmt,jdbcType=DECIMAL},
  UPDATE_USERID = #{updateUserid,jdbcType=INTEGER},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where QR_TBSDY_INTL = #{qrTbsdyIntl,jdbcType=CHAR}
  and QR_PCODE = #{qrPcode,jdbcType=CHAR}
  and QR_ZONE_CODE = #{qrZoneCode,jdbcType=CHAR}
  and QR_ACQ_CNTRY = #{qrAcqCntry,jdbcType=CHAR}
