mapper.select:

select 

from WKPOSTDTL
where SYSCODE = #{syscode,jdbcType=CHAR}
  and TXDATE = #{txdate,jdbcType=DATE}
  and SEQNO = #{seqno,jdbcType=INTEGER}

mapper.delete:

delete from WKPOSTDTL
where SYSCODE = #{syscode,jdbcType=CHAR}
  and TXDATE = #{txdate,jdbcType=DATE}
  and SEQNO = #{seqno,jdbcType=INTEGER}
